package com.yujing.socket.test;

import com.yujing.utils.YConvert;

import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.ServerSocket;
import java.net.Socket;
import java.util.Arrays;

/**
 * 服务器端
 */
public class SerTest {
	public static void main(String[] args) {
		System.out.println("开始");
		try {
			ServerSocket server = new ServerSocket(5555);
			while (!server.isClosed()) {
				final Socket socket = server.accept();
				new Thread(() -> {
					while (true) {
						try {
							String hex="";
							byte[] bs= YConvert.hexStringToByte(hex);
							OutputStream os=socket.getOutputStream();
							os.write(bs);
							os.flush();
						} catch (IOException e) {
							return;
						}
						try {
							Thread.sleep(1000 * 10);
						} catch (InterruptedException ignored) {
						}
					}
				}).start();
				new Thread(() -> {
					while (true) {

							try {
								InputStream is = socket.getInputStream();
								System.out.println("收到："+Arrays.toString(inputStreamToBytes(is)));
							} catch (Exception e) {
								System.out.println("ReadThread：" + e.getMessage());
								// 失败3秒后重新读取
								try {
									Thread.sleep(3000);
								} catch (InterruptedException ignored) {
								}
							}

					}
				}).start();
			}

		} catch (Exception ignored) {
		}
	}

	public static byte[] inputStreamToBytes(InputStream inputStream) throws IOException {
		// 网络传输时候，这样获取真正长度
		int count = 0;
		while (count == 0) {
			count = inputStream.available();
		}
		byte[] bytes = new byte[count];
		// 一定要读取count个数据，如果inputStream.read(bytes);可能读不完
		int readCount = 0; // 已经成功读取的字节的个数
		while (readCount < count) {
			readCount += inputStream.read(bytes, readCount, count - readCount);
		}
		return bytes;
	}
}
